# c44 c50 c55

c44 = int(base64.b64decode(open('c44.txt','r').read()))
c50 = int(base64.b64decode(open('c50.txt','r').read()))
c55 = int(base64.b64decode(open('c55.txt','r').read()))

ciphertexts = []
ciphertexts.extend((c55,c50,c44))
result = []
for ct in ciphertexts:
	pt = pow(ct,d,n)
	pt = str(hex(pow(ct, d, n)))[2::]
	result2 = ''.join([chr(int(''.join(c), 16)) for c in zip(pt[0::2],pt[1::2])])
	result.append(result2)
result_final = ''.join(result)
print('flag{' + result_final + '}')

